package com.neighbor.near;

public class WordArr {

	String word;
	String prim;
	String neighbor;
	int level;
	
	public void setword(String myword) {
		// TODO Auto-generated method stub
		word = myword;
	}

	public void setprim(String myword){
		prim = myword;
	}
	
	public void setneighbor(String myword){
		neighbor = myword;
	}

	public void setlevel(int i) {
		// TODO Auto-generated method stub
		level = i;
	}

	public String getprim() {
		// TODO Auto-generated method stub
		return prim;
	}

	public String getneighbor() {
		// TODO Auto-generated method stub
		return neighbor;
	}
}
